import React from 'react'

class Dog extends React.Component {
    buttonRender = () => {
        if(this.props.dog.isGoodDog){
            return <button onClick={() => this.props.switch(this.props.dog)}>Not good anymore</button>
        }else if (this.props.dog.isGoodDog){
            return <button onClick={() => this.props.switch(this.props.dog)}>Is a good boy now</button>
        }else{
            return null
        }
    }
    render() {
        return (
            <div id="dog-info">
                <img src={this.props.dog.image} alt={this.props.dog.name} />
                {this.props.dog.name}
                <button></button>
            </div>
        )
    }
}

export default Dog
