import React from 'react';
import './App.css';
import Filter from './containers/Filter.js'

const dogAPI = "http://localhost:3000/pups/"
class App extends React.Component {
  state = {
    dogs: [],
    selected: {}
  }

  componentDidMount = () => {
    fetch(dogAPI)
    .then(resp => resp.json())
    .then(pups =>{
      this.setState({dogs: pups})
    })
  }
  render(){
  return (
    <div className="App">
      {console.log(this.state)}
      <Filter />
      <DogContainer />
    </div>
  );
}
}
export default App;
