import './App.css';

const BASE_URL = "http://localhost:3000/pups"
const dogArray = []

function App() {

  function renderDogs() {
    fetch(BASE_URL)
    .then(resp => resp.json())
    .then(dogs => {
      dogArray = dogs
    })
  }
  console.log(dogArray)
  return (

    <div className="App">
      <div id="filter-div">
        <button id="good-dog-filter">Filter good dogs: OFF</button>
      </div>
      <div id="dog-bar">
        {/* {renderDogs()} */}
      </div>
      <div id="dog-summary-container">
        <h1>DOGGO:</h1>
        <div id="dog-info">

        </div>
      </div>
    </div>
  );
}

export default App;
