import React from 'react';
import './App.css';
import DogsBar from './Containers/DogsBar'

const BASE_URL = "http://localhost:3000/pups"

class App extends React.Component {

    state = {
      dogArray: [],
      dog: []
    }

    componentDidMount = () => {
      fetch(BASE_URL)
      .then(resp => resp.json())
      .then(dogs => this.setState({
        dogArray: dogs
      }))
    }

    renderDog = dogObj => {
      let dog = this.state.dogArray.filter(el => el.name === dogObj.textContent)
      console.log(dog)
    }

  render() {
    return ( 
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>
        <div id="dog-bar">
          < DogsBar renderDog={this.renderDog} dogs={this.state.dogArray}/>
        </div>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">

          </div>
        </div>
      </div>
    )
  }

}

export default App;
